use std::collections::HashMap;

#[derive(Debug, Clone)]
struct Царство {
    имя: String,
    герои: Vec<Герой>,
    сокровища: HashMap<String, i32>,
}

impl Царство {
    fn новое(имя: &str) -> Self {
        Self {
            имя: имя.to_string(),
            герои: Vec::new(),
            сокровища: HashMap::new(),
        }
    }

    async fn процветать(&mut self) -> Result<(), Error> {
        for герой in &mut self.герои {
            герой.расти().await?;
        }
        Ok(())
    }
}

