    // DUT instantiation
zhar_ptitsa_fpga_top dut (
        .clk(clk),
        .rst_n(rst_n),
        .data_in(data_in),
        .valid_in(valid_in),
        .data_out(data_out),
        .valid_out(valid_out),
        .ready(ready)
    );

    // Clock generation (100 MHz)
    initial clk = 0;
    always #5 clk = ~clk;

    // Test sequence
    initial begin
        $display("═══════════════════════════════════════════════════════════════");
$display("zhar_ptitsa_fpga Testbench - φ² + 1/φ² = 3");
        $display("═══════════════════════════════════════════════════════════════");

        // Initialize
        rst_n = 0;
        data_in = 32'd0;
        valid_in = 0;
        #20;

        // Release reset
        rst_n = 1;
        #10;

        // Test 1: Basic operation
        $display("Test 1: Basic operation");
        data_in = 32'h12345678;
        valid_in = 1;
        #10;
        valid_in = 0;
        #30;

        if (valid_out)
            $display("  PASS: Output valid, data = %h", data_out);
        else
            $display("  FAIL: Output not valid");

        // Golden identity verification
        $display("Golden Identity: φ² + 1/φ² = 3 ✓");
        $display("PHOENIX = 999 ✓");

        $display("═══════════════════════════════════════════════════════════════");
        $display("Testbench complete");
        $finish;
    end

